<?php

  Function post($par)
  {
    return strip_tags(trim(htmlspecialchars(addslashes($_POST[$par]))));
  }

  Function get($par)
  {
    return strip_tags(trim(htmlspecialchars(addslashes($_GET[$par]))));
  }

  Function p($par)
  {
    return addslashes(trim($_POST[$par]));
  }

  Function g($par)
  {
    return strip_tags(trim(addslashes($_GET[$par])));
  }

  Function ss($par)
  {
    return stripslashes($par);
  }

  Function go($par, $time = 0)
  {
    if($time == 0){
      header("Location: {$par}");
    } else {
      header("Refresh: {$time}; url={$par}");
    }
  }

  Function includeFile($file)
  {
    include realpath(dirname(__FILE__)).'/'.$file;
  }

  Function password($id)
  {
    $intermediateSalt = md5(uniqid(rand(), true));
    $salt = substr($intermediateSalt, 0, MAX_LENGTH);
    return hash("sha256", $id . $salt);
  }

  Function randomKey($length = 11)
  {
    return substr(str_shuffle(str_repeat($x='0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ', ceil($length/strlen($x)) )),1,$length);
  }

  Function timeAgo($time)
  {
    $periods = array("saniye", "dakika", "saat", "gün", "hafta", "ay", "yıl", "decade");
    $lengths = array("60","60","24","7","4.35","12","10");
    $now = time();
    $difference  = $now - strtotime($time);
    for($j = 0; $difference >= $lengths[$j] && $j < count($lengths)-1; $j++)
    {
      $difference /= $lengths[$j];
    }
    $difference = round($difference);
    return "$difference $periods[$j] önce";
  }

