  Function slug()
  {
    if(MULTILANG)
    {
      if(PSLUG4)
      {
        return PSLUG1.'/'.PSLUG2.'/'.PSLUG3.'/'.PSLUG4;
      }
      else if(PSLUG3)
      {
        return PSLUG1.'/'.PSLUG2.'/'.PSLUG3;
      }
      else if(PSLUG2)
      {
        return PSLUG1.'/'.PSLUG2;
      }
      else if(PSLUG1)
      {
        return PSLUG1;
      }
    }
    else
    {
      if(PSLUG3)
      {
        return PSLUG0.'/'.PSLUG1.'/'.PSLUG2.'/'.PSLUG3;
      }
      else if(PSLUG2)
      {
        return PSLUG0.'/'.PSLUG1.'/'.PSLUG2;
      }
      else if(PSLUG1)
      {
        return PSLUG0.'/'.PSLUG1;
      }
      else if(PSLUG0)
      {
        return PSLUG0;
      }
    }
  }

  Function __($value,$lang=null)
  {
    $rLang = $lang ? $lang : LANG;
    $explode = explode('{lang}', $value);
    if($rLang=='tr')
    {
      $value = $explode['0'];
    }
    else if($rLang=='en')
    {
      $value = $explode['1'];
    }
    else if(!MULTILANG)
    {
      $value = $explode['0'];
    }
    else if(MULTILANG and !$lang)
    {
      $value = $explode['0'];
    }
    return ss(ltrim(rtrim($value)));
  }

  Function alert($status,$title,$message,$position=null)
  {
    $position = $position ? $position : "topRight";
    echo "<script>iziToast.".$status."({title: '".$title."',message: '".$message."', position: '".$position."'});</script>";
  }

  function isMobile() {
    return preg_match("/(android|avantgo|blackberry|bolt|boost|cricket|docomo|fone|hiptop|mini|mobi|palm|phone|pie|tablet|up\.browser|up\.link|webos|wos)/i", $_SERVER["HTTP_USER_AGENT"]);
}
